static const char hex_to_char[16] = \
{
    /* '0' - '9' */
    0x30, 0x31, 0x32, 0x33, 0x34, 0x35, 0x36, 0x37, 0x38, 0x39,
    /* 'A' - 'F' */
    0x41, 0x42, 0x43, 0x44, 0x45, 0x46
};

void SSMHexToChar(PCBYTE pbHex, char *pcChar, UINT nSize)
{
    int i;

    for (i = 0; i < nSize; i++)
    {
        pcChar[2*i] = hex_to_char[((pbHex[i] & 0xF0) >> 4)];
        pcChar[2*i + 1] = hex_to_char[(pbHex[i] & 0x0F)];
    }

    return;
}

void SSMMakeOddBinStr(PBYTE pbBin, UINT nLen)
{
    int i;
    BYTE x;

    for(i = 0; i < nLen; i++)
    {
        x = pbBin[i];

        x = ((x & 0x55) + ((x >> 1) & 0x55));
        x = ((x & 0x33) + ((x >> 2) & 0x33));
        x = ((x & 0x0f) + ((x >> 4) & 0x0f));
        if((x & 0x01) == 0)
        {
            pbBin[i] ^= 0x01;
        }
    }

    return;
}

